refsection.programlisting:
CREATE DATABASE bridge
USE bridge
GRANT ALL PRIVILEGES ON bridge.* TO bridge@localhost IDENTIFIED BY '<password>' WITH GRANT OPTION;
GRANT ALL PRIVILEGES ON bridge.* TO bridge@localhost.localdomain IDENTIFIED BY '<password>' WITH GRANT OPTION;
GRANT ALL PRIVILEGES ON bridge.* TO bridge@<server> IDENTIFIED BY '<password>' WITH GRANT OPTION;
EXIT

